mapper.select:
        SELECT * FROM cn_note 


       		cn_note_title like #{cn_note_title} 


	        	and 
	        	cn_note_status_id=#{cn_note_status_id}  


	            and 
	        	cn_note_create_time>#{beginTime} 


	            and
	        	cn_note_create_time<#{endTime}

mapper.update:
update cn_note
cn_note_title=#{cn_note_title}, cn_note_status_id=#{cn_note_status_id}, cn_notebook_id=#{cn_notebook_id}, cn_note_body=#{cn_note_body}, cn_note_last_modify_time={cn_note_last_modify_time}
where cn_note_id=#{cn_note_id}
